import React, { useState } from "react";

function CreateArea(props) {
  const [task, setTask] = useState({
    title: "",
    description: "",
    status: "To-Do",
    dueDate: ""
  });

  const [tasks, setTasks] = useState([]);

  function handleChange(event) {
    const { name, value } = event.target;

    setTask(prevTask => {
      return {
        ...prevTask,
        [name]: value
      };
    });
  }

  function submitTask(event) {
    setTasks([...tasks, task]);
    setTask({
      title: "",
      description: "",
      status: "To-Do",
      dueDate: ""
    });
    event.preventDefault();
  }

  function updateTask(index, updatedTask) {
    setTasks(tasks.map((task, i) => (i === index ? updatedTask : task)));
  }

  function deleteTask(index) {
    setTasks(tasks.filter((task, i) => i !== index));
  }

  return (
    <div>
      <h1>Task Manager</h1>
      <form>
        <input
          name="title"
          onChange={handleChange}
          value={task.title}
          placeholder="Title"
        />
        <textarea
          name="description"
          onChange={handleChange}
          value={task.description}
          placeholder="Description"
          rows="3"
        />
        <select name="status" onChange={handleChange} value={task.status}>
          <option value="To-Do">To-Do</option>
          <option value="In Progress">In Progress</option>
          <option value="Done">Done</option>
        </select>
        <input
          type="date"
          name="dueDate"
          onChange={handleChange}
          value={task.dueDate}
          placeholder="Due Date"
        />
        <button onClick={submitTask}>Add Task</button>
      </form>
      <ul>
        {tasks.map((task, index) => (
          <li key={index}>
            <h2>{task.title}</h2>
            <p>{task.description}</p>
            <p>Status: {task.status}</p>
            <p>Due Date: {task.dueDate}</p>
            <button onClick={() => updateTask(index, { ...task, status: "In Progress" })}>
              Start
            </button>
            <button onClick={() => updateTask(index, { ...task, status: "Done" })}>
              Complete
            </button>
            <button onClick={() => deleteTask(index)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default CreateArea;
